import React,{useState,useEffect} from 'react'
import {useSelector} from 'react-redux'
import { Bar } from 'react-chartjsx'

const ChartData = () =>{ 

                const {DATA_TABLE_USER} = useSelector(state => ({...state.ChartReducer}))
                //wlanBytes : Dung Lượng
                const [wlanBytes,setWlanBytes] = useState()
                //numsta: Số Lượng
                const [numsta,setNumsta] = useState()
                //time: thời gian
                const [time,setTime] = useState()

            useEffect(() => {
                 const wlanBytes = []
                 const numsta = []
                 const time = []
                 DATA_TABLE_USER.forEach(data => {
                    wlanBytes.push(data.wlan_bytes)
                    numsta.push(data.num_sta)
                    time.push(data.time)
                });
                setWlanBytes(wlanBytes)
                setNumsta(numsta)
                setTime(time)
            },[DATA_TABLE_USER,wlanBytes,numsta,time])



            const mixedChartsData = {
                labels: time,
                datasets: [{ 
                    label: "Dung Lượng", 
                    type: "line", 
                    borderColor: "#8e5ea2", 
                    data: wlanBytes, 
                    fill: false 
                }, { 
                    label: "Số Lượng", 
                    type: "bar", 
                    backgroundColor: "#c45850", 
                    data: numsta, 
                }
                ] 
            }


    return (
        <div>
            <Bar data={mixedChartsData} 
             width={600} 
             height={400} /> 
            </div>
    )
}

export default ChartData
